import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import BlogMeta from "../components/BlogMeta";
export const meta = {
  title: "Next.js MDX Blog Kit",
  tags: ["Next.js", "MDX"],
  layout: "post",
  publishDate: "2011-01-01",
  modifiedDate: false,
  seoDescription: "This is the home page."
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <h1>{`Pedro Paulo Bazzo`}</h1>
    <h1>{`Blog Kit`}</h1>
    <p>{`Check out the `}<a parentName="p" {...{
        "href": "https://github.com/lorenseanstewart/nextjs-mdx-blog-kit"
      }}>{`docs`}</a></p>
    <p><strong parentName="p">{`Feature List:`}</strong></p>
    <ul>
      <li parentName="ul">{`Static website deployment`}</li>
      <li parentName="ul">{`Write your posts in markdown, and use React components in your markdown thanks to `}<a parentName="li" {...{
          "href": "https://github.com/mdx-js/mdx"
        }}>{`MDX`}</a>{`. Or use plain React, if you prefer.`}</li>
      <li parentName="ul">{`A CLI for creating new pages or posts. It processes all the necessary meta data for creating the blog list and adding SEO data to all pages.`}</li>
      <li parentName="ul">{`Perfect Lighthouse scores.`}</li>
      <li parentName="ul">{`Excellent SEO`}</li>
      <li parentName="ul">{`Service Worker that caches all pages and posts for offline reading`}</li>
      <li parentName="ul">{`Tag search`}</li>
      <li parentName="ul">{`A `}<a parentName="li" {...{
          "href": "https://github.com/jeremenichelli/scrollProgress"
        }}>{`reading progress indicator`}</a>{` on posts (optional/configurable)`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/conorhastings/react-syntax-highlighter"
        }}>{`Code syntax highlighting`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/mauricevancooten/react-anchor-link-smooth-scroll"
        }}>{`Smooth scroll links`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/cyrilwanner/next-optimized-images"
        }}>{`Optimized images`}</a></li>
      <li parentName="ul">{`Tests are set up using `}<a parentName="li" {...{
          "href": "https://github.com/facebook/jest"
        }}>{`Jest`}</a></li>
      <li parentName="ul">{`Easy deploys with Zeit's `}<a parentName="li" {...{
          "href": "https://zeit.co/now"
        }}>{`Now`}</a></li>
    </ul>
    <p><strong parentName="p">{`View Source:`}</strong>{` If you view the source of this webpage, you will see that pages are rendered and minified on a server and served as static files.`}</p>
    <p><strong parentName="p">{`Lighthouse:`}</strong>{` If you want first-hand evidence of the audit scores shown in the project README.md, feel free to run a Lighthouse audit of this site. `}<em parentName="p">{`Scores are more accurate when run in an incognito browser because Chrome extensions can adversely affect scores.`}</em></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
